import Panel from './panel';

const deckStyle = {
  width: 150,
  textAlign: 'center',
  lineHeight: 0,
}

const headerStyle = {
  width: 150,
  lineHeight: '37px',
}

const deckList = Array(5).fill(null)

const Deck = (props) => (
  <div style={ deckStyle }>
    <h1 style={headerStyle}>Player {props.player}</h1>
    { deckList.map((item, index) => <Panel>{index}</Panel>) }
  </div>
)

export default Deck;
