import Panel from './panel';
import Deck from './deck';

const panels = Array(9).fill(null)
const boardStyle = {
  width: 150
}

const Board = () => (
  <div style={ boardStyle }>
    {panels.map((item, index) => <Panel index={ index }></Panel>)}
  </div>
);

export default Board;
