const cardStyle = {
  position: 'relative',
  height: '100%',
  width: '100%',
}

const northStyle = {
  position: 'absolute',
  top: 0,
  left: '50%',
  transform: 'translateX(-50%)',
}

const eastStyle = {
  position: 'absolute',
  top: 35,
  right: 0,
}

const southStyle = {
  position: 'absolute',
  bottom: 0,
  left: '50%',
  transform: 'translateX(-50%)',
}

const westStyle = {
  position: 'absolute',
  top: 35,
  left: 0,
}

const Card = (props) => (
  <div style={ cardStyle }>
    <span style={ northStyle }>{ props.north }</span>
    <span style={ eastStyle }>{ props.east }</span>
    <span style={ southStyle }>{ props.south }</span>
    <span style={ westStyle }>{ props.west }</span>
  </div>
);

export default Card;
